import React from "react";

class Search extends React.Component {
    state = {
        search: 'scooby doo',
        type: 'all'
    };
    handleKey = (event) => {
        if (event.key === 'Enter') {
            this.props.searchMovies(this.state.search, this.state.type);
        }
    };

    handleFilter = (event) => {
        this.setState(
            () => ({ type: event.target.dataset.type }),
            () => {
                this.props.searchMovies(this.state.search, this.state.type);
            }
        );
    };

    render() {
        return <div className="row">
            <div className="col s12">
                <p>Привет, здесь ты можешь найти фильм, который хочешь посмотреть :3</p>
                <div className="input-field">
                    <input
                        className="validate"
                        placeholder="search"
                        type="search" 
                        value={this.state.search}
                        onChange={(e) => this.setState({ search: e.target.value })}
                        onKeyDown={this.handleKey}
                    />
                    <button className="btn search-btn teal lighten-2" onClick={() => this.props.searchMovies(this.state.search, this.state.type)}> Search </button>
                </div>
                <div>
                     <label>
                        <input className="with-gap" name="type" type="radio" data-type="all" onChange={this.handleFilter} checked={this.state.type === "all"}/>
                        <span>All</span>
                    </label>
                    <label>
                        <input className="with-gap" name="type" type="radio" data-type="movie" onChange={this.handleFilter} checked={this.state.type === "movie"}/>
                        <span>Movies</span>
                    </label>
                    <label>
                        <input className="with-gap" name="type" type="radio" data-type="series" onChange={this.handleFilter} checked={this.state.type === "series"}/>
                        <span>Series</span>
                    </label>
                    
                </div>
            </div>
        </div>
    }
}
export { Search };
